<script setup lang="ts">
import { ref, onMounted, onUnmounted, watch } from "vue";

const mapContainer = ref<HTMLElement | null>(null);
let map: any = null;
const markers: any[] = [];
const stores = ref([]);

const config = useRuntimeConfig();
const jawgMapsApiKey = config.public.jawgMapsApiKey;

const initMap = async () => {
  if (!mapContainer.value) return;

  // Dynamic import of Leaflet
  const L = await import("leaflet");

  map = L.map(mapContainer.value).setView([0, 0], 2);

  L.tileLayer(
    `https://tile.jawg.io/jawg-streets/{z}/{x}/{y}.png?access-token=${jawgMapsApiKey}`,
    {
      attribution:
        '<a href="http://jawg.io" title="Tiles Courtesy of Jawg Maps" target="_blank">&copy; <b>Jawg</b>Maps</a> &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      minZoom: 0,
      maxZoom: 22,
    }
  ).addTo(map);
};

const addMarkers = async (stores) => {
  if (!map) return;

  const L = await import("leaflet");

  // Clear existing markers
  markers.forEach((marker) => marker.remove());
  markers.length = 0;

  stores.forEach((store) => {
    const marker = L.marker([store.latitude, store.longitude])
      .bindPopup(
        `
        <div class="p-2">
          <h3 class="font-semibold">${store.name}</h3>
          <p>${store.address}</p>
        </div>
      `
      )
      .addTo(map);

    markers.push(marker);
  });

  // Fit bounds to show all markers
  if (markers.length > 0) {
    const group = L.featureGroup(markers);
    map.fitBounds(group.getBounds(), { padding: [50, 50] });
  }
};

// Fetch data from the API
const fetchStores = async () => {
  try {
    const response = await $fetch("/api/store", {
      // Ajout des options pour mieux grer les erreurs
      onResponse({ response }) {
        console.log("API Response:", response._data);
      },
      onRequestError({ error }) {
        console.error("Request Error:", error);
        throw error;
      },
      onResponseError({ response }) {
        console.error("Response Error:", response._data);
        throw new Error(response._data?.message || "Failed to fetch stores");
      },
    });

    stores.value = response;
  } catch (err) {
    console.error("Error fetching stores:", err);
    // Vous pouvez ajouter ici une gestion d'erreur UI
    // Par exemple :
    // error.value = err.message
  }
};

// Watch for changes in `stores` and update markers
watch(
  stores,
  (newStores) => {
    if (newStores) {
      addMarkers(newStores);
    }
  },
  { deep: true }
);

onMounted(async () => {
  await fetchStores(); // Fetch data on mount
  await initMap(); // Initialize the map
});

onUnmounted(() => {
  if (map) {
    map.remove();
    map = null;
  }
});
</script>
